import React, {useContext, useEffect} from 'react';
import { Link } from 'react-router-dom'
import assetContext from '../context/asset/assetContext'
import TreeMenu from 'react-simple-tree-menu'



const SidebarDescriptors = () => {


    const aContext = useContext(assetContext)
    const {assetsTree, getAssetTree} = aContext

    let tree_assets = [{}]

    useEffect(() => {
        const initAssetTree = () =>{
            getAssetTree();
            let j = 0
            let i = 0
            assetsTree.forEach(i_asset => {
                tree_assets[j].key = i_asset.name
                tree_assets[j].label = i_asset.name
                i_asset.forEach(i_system => {
                    tree_assets[j].nodes[i].key = i_system.name
                    tree_assets[j].nodes[i].label = i_system.name
                    i = i+1;
                })
                j= j+1;
            });
        }
        initAssetTree()


    }, [])

    console.log(assetsTree);


    return ( 
        <aside>
            <h1>Estructura<span>MdP</span></h1>

            <div className="proyectos">

                <h2>Assets</h2>  
                {/* <TreeMenu data={treeData} /> */}
            </div>  
            <div className="link-menu-div">
                <Link 
                    to={'/menu'}
                    className="link-menu">
                &#60;
                    Menu
                </Link>
            </div>
        </aside>

     );
}

export default SidebarDescriptors;
