import React, {useContext, useEffect, Fragment} from 'react';
import {useParams} from 'react-router-dom'
import tagDescriptorContext from '../../context/tagdescriptor/tagDescriptorContext'

const ShowTagDescritor = () => { 
    const { tagname } = useParams();
  //return <h3>Requested topic ID: {topicId}</h3>;
    const tContext = useContext(tagDescriptorContext)
    const {getTagDescriptor, tagdescriptor} = tContext;

    useEffect(() => {
        getTagDescriptor(tagname)
    }, [])

    return ( 
        <Fragment>
            {
                tagdescriptor ?

                (
                <div>
                    <h1>TagDescriptor para el {tagname}</h1>
                    <p>{tagdescriptor.tagname}</p>
                    <p>{tagdescriptor.description}</p>
                </div>
                )

            :
                (<h1>No se encontr√≥ descriptor {tagname}</h1>)
           }
        </Fragment>

     );
}

export default ShowTagDescritor;
